package for_loop;

public class WithoutForLoop_NaturalNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		System.out.println(i + " ");
		i++;
		System.out.println(i + " ");
		i++;
		System.out.println(i + " ");
		i++;
		System.out.println(i + " ");
		i++;
		
		for(int a=1; a<=10; a++) {
			System.out.print(a+ " ");
		}
	}

}
